                                        <script>
                                            document.addEventListener("DOMContentLoaded", function() {
                                                document.querySelectorAll(".status-option").forEach(function(option) {
                                                    option.addEventListener("click", function(event) {
                                                        event.preventDefault(); // منع التنقل بالـ href
                                                        let status = this.getAttribute("data-value");
                                                        let form = this.closest("form");
                                                        form.querySelector("input[name='status']").value = status;
                                                        form.submit(); // إرسال النموذج تلقائيًا
                                                    });
                                                });
                                            });
