<script>
    $(document).ready(function () {
        $('#auctionForm').submit(function (e) {
            e.preventDefault();

            let form = $(this);
            let formData = form.serialize(); // تجميع بيانات النموذج
            let submitButton = $('#bidButton');
            let responseMessage = $('#responseMessage');

            submitButton.prop('disabled', true).text('Processing...');

            $.ajax({
                url: form.attr('action'), 
                type: 'POST',
                data: formData,
                success: function (response) {
                    responseMessage.html('<div class="alert alert-success">Your bid has been placed successfully!</div>');
                    form.trigger('reset'); 
                },
                error: function (xhr) {
                    let errorMessage = xhr.responseJSON ? xhr.responseJSON.message : 'An error occurred!';
                    responseMessage.html('<div class="alert alert-danger">' + errorMessage + '</div>');
                },
                complete: function () {
                    submitButton.prop('disabled', false).text('Bidding');
                }
            });
        });
    });
</script>

    
@endsection

